mapper.select:
select mk.id as id, mk.user_id as user_id, mk.category_id as category_id, mk.transaction_date as transaction_date, mk.updated_at as updated_at, c.name as category_name, mk.type as type, mk.amount as amount, mk.notes as notes from moneykeeper as mk inner join categories as c on mk.category_id = c.id where mk.deleted_at = 0 and c.deleted_at = 0
AND mk.user_id = #{userId}
AND mk.transaction_date >= #{startDate}
AND mk.transaction_date <= #{endDate}
order by mk.updated_at desc

select mk.id as id, mk.user_id as user_id, mk.category_id as category_id, mk.transaction_date as transaction_date, mk.updated_at as updated_at, c.name as category_name, mk.type as type, mk.amount as amount, mk.notes as notes from moneykeeper as mk inner join categories as c on mk.category_id = c.id where mk.deleted_at = 0 and c.deleted_at = 0
AND mk.user_id = #{userId}
AND c.name = #{categoryName}
AND transaction_date >= #{startDate}
AND transaction_date <= #{endDate}
order by mk.updated_at desc

SELECT COALESCE(SUM(CASE WHEN type = '收入' THEN amount ELSE 0 END), 0) as totalIncome, COALESCE(SUM(CASE WHEN type = '支出' THEN amount ELSE 0 END), 0) as totalExpense, COALESCE(SUM(CASE WHEN type = '收入' THEN amount ELSE -amount END), 0) as balance FROM moneykeeper mk WHERE user_id = #{userId} and mk.deleted_at = 0
AND transaction_date >= #{startDate}
AND transaction_date <= #{endDate}

SELECT * FROM moneykeeper
WHERE user_id = #{userId} and deleted_at = 0

    AND transaction_date >= #{startDate}


    AND transaction_date <= #{endDate}

ORDER BY transaction_date DESC
